import {Button, Form} from 'react-bootstrap';
//import JoditEditor from 'jodit-react';
import React, {useState, useRef} from 'react';


const Mailer = () => {

    //const editor = useRef(null);
    const [to, setTo] = useState('');
    const [subject, setSubject] = useState('');
    const [message, setMessage] = useState('');



    const sendEmail = (e) => {
        e.preventDefault();

        // emailjs.sendForm('service_ht917hd', 'template_2j6ta1b', form.current, 'mKzr97VhPi-Cr5F-X')
        // .then((result) => {
        //     console.log(result.text);
        // }, (error) => {
        //     console.log(error.text);
        // });

        // e.target.reset();

        const mailData = {
            to : to,
            subject : subject,
            message: message
        }
        console.log(mailData);

    }


  return (
    <Form onSubmit={sendEmail}>
      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>To : </Form.Label>
        <Form.Control type="email" placeholder="Enter email" value={to} onChange={(e) => setTo(e.target.value)} required/>
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicSubject">
        <Form.Label>Subject</Form.Label>
        <Form.Control type="text" placeholder="Subject" value={subject} onChange={(e) => setSubject(e.target.value)} />
      </Form.Group>
      <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea1">
        <Form.Label>Example textarea</Form.Label>
        <Form.Control as="textarea" rows={3} />
      </Form.Group>
      <Button variant="primary" type="submit">
        Submit
      </Button>
    </Form>
  );
}

export default Mailer;
