import React, { useState } from 'react';
import { Navbar, Nav, Container, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { getAuth, signOut } from "firebase/auth";
import { useNavigate } from 'react-router-dom';
import { Alert } from 'react-bootstrap';

const Header = () => {
  const navigate = useNavigate();
  const [error, setError] = useState(null);
  const [currentComponent, setCurrentComponent] = useState('home'); // Default component to display

  const logoutHandler = () => {
    const auth = getAuth();
    signOut(auth)
      .then(() => {
        alert("Signout Successful");
      })
      .catch((error) => {
        const errorMessage = error.message;
        setError(errorMessage);
      });
    navigate("/");
  };

  const handleNavClick = (component) => {
    setCurrentComponent(component);
  };

  return (
    <div>
      <Navbar bg="light" expand="lg">
        <Navbar.Brand href="/home">Your Brand Name</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="mr-auto">
            <Nav.Link as={Link} to="/home" onClick={() => handleNavClick('home')}>
              Home
            </Nav.Link>
            <Nav.Link as={Link} to="/inbox" onClick={() => handleNavClick('inbox')}>
              Inbox
            </Nav.Link>
            <Nav.Link as={Link} to="/sent" onClick={() => handleNavClick('sent')}>
              Sent
            </Nav.Link>
          </Nav>
          <Nav>
            <Button variant="outline-primary" onClick={logoutHandler}>
              Logout
            </Button>
          </Nav>
        </Navbar.Collapse>
        {error && <Alert variant='danger'>{error}</Alert>}
      </Navbar>

      {/* Conditionally render the appropriate component */}
      {currentComponent === 'home' && <HomeComponent />}
      {currentComponent === 'inbox' && <InboxComponent />}
      {currentComponent === 'sent' && <SentComponent />}
    </div>
  );
};

export default Header;
