import {Button, Form} from 'react-bootstrap';
import JoditEditor from 'jodit-react';
import React, {useState, useRef} from 'react';
import emailjs from '@emailjs/browser';


const Mailer = () => {

    //const editor = useRef(null);
    const [to, setTo] = useState('');
    const [subject, setSubject] = useState('');
    //const [message, setMessage] = useState('');
    //const form = useRef();
    const editor = useRef(null);
	const [content, setContent] = useState('');


    const sendEmail = (e) => {
        e.preventDefault();

        // emailjs.sendForm('service_ht917hd', 'template_2j6ta1b', form.current, 'mKzr97VhPi-Cr5F-X')
        // .then((result) => {
        //     console.log(result.text);
        // }, (error) => {
        //     console.log(error.text);
        // });

        // e.target.reset();

        const mailData = {
            to : to,
            subject : subject,
            content: content
        }
        console.log(mailData);

    }


  return (
    <Form onSubmit={sendEmail}>
      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>To : </Form.Label>
        <Form.Control type="email" placeholder="Enter email" value={to} onChange={(e) => setTo(e.target.value)} required/>
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicSubject">
        <Form.Label>Subject</Form.Label>
        <Form.Control type="text" placeholder="Subject" value={subject} onChange={(e) => setSubject(e.target.value)} />
      </Form.Group>
      <JoditEditor 
            ref={editor}
			value={content}
			tabIndex={1} // tabIndex of textarea
			onBlur={newContent => setContent(newContent)} // preferred to use only this option to update the content for performance reasons
			onChange={(e) => setContent(e.target.value)}
             />
      <Button variant="primary" type="submit">
        Submit
      </Button>
    </Form>
  );
}

export default Mailer;
