import React, { Fragment, useEffect, useState } from 'react';
import { Accordion } from 'react-bootstrap';

const Sent = () => {
    const [mailDataList, setMailDataList] = useState([]);

    useEffect(() => {
        fetch('https://mail-box-client-22a46-default-rtdb.firebaseio.com/mailData.json')
        .then((res) => {
            if (res.ok) {
                return res.json(); // Parse response body as JSON
            }
            throw new Error('Something went wrong');
        })
        .then((data) => {
            const mailData = [];
            for (const key in data) {
                mailData.push({
                    id: key,
                    to: data[key].to,
                    subject: data[key].subject,
                    message: data[key].message,
                });
            }
            setMailDataList(mailData);
        })
        .catch((err) => {
            alert(err);
        });
    }, []);

    return (
        <Fragment>
            <ul>
                {mailDataList.map((mail) => (
                     <Accordion>
                     <Accordion.Item eventKey={mail.id}>
                       <Accordion.Header>To: {mail.to}, Subject: {mail.subject}</Accordion.Header>
                       <Accordion.Body>
                        {mail.message}
                       </Accordion.Body>
                     </Accordion.Item>
                   </Accordion>
                ))}
            </ul>
        </Fragment>
    );
};

export default Sent;
