import React, { Fragment, useEffect, useState } from 'react';
import { Accordion } from 'react-bootstrap';

const Inbox = () => {
    const [mailDataList, setMailDataList] = useState([]);
    const [ username, setUsername] = useState('');
    const [ email, setEmail] = useState('');
    const [ password, setPassword] = useState('');

    useEffect(() => {
        fetch('https://mail-box-client-22a46-default-rtdb.firebaseio.com/mailData.json')
            .then((res) => {
                if (res.ok) {
                    return res.json(); // Parse response body as JSON
                }
                throw new Error('Something went wrong');
            })
            .then((data) => {
                const mailData = [];
                for (const key in data) {
                    mailData.push({
                        id: key,
                        to: data[key].to,
                        subject: data[key].subject,
                        message: data[key].message,
                    });
                }
                setMailDataList(mailData);
            })
            .catch((err) => {
                alert(err);
            });

        fetch('https://mail-box-client-22a46-default-rtdb.firebaseio.com/userData.json')
            .then((res) => {
                if (res.ok) {
                    return res.json(); // Parse response body as JSON
                }
                throw new Error('Something went wrong');
            })
            .then((data) => {
                setEmail(data.email);
                setUsername(data.username);
                setPassword(data.password);
            })
            .catch((err) => {
                alert(err);
            });


    }, []);



    return (
        <Fragment>
            {mailDataList.map((mail) => {

                if (mail.to === email) {
                    return (
                        <Accordion>
                            <Accordion.Item eventKey={mail.id}>
                                <Accordion.Header>From: {mail.to}, Subject: {mail.subject}</Accordion.Header>
                                <Accordion.Body>
                                    {mail.message}
                                </Accordion.Body>
                            </Accordion.Item>
                        </Accordion>
                    );
                }
                else{
                    return(
                        <div>No mails in the Inbox</div>
                    );
                }


            })}
        </Fragment>
    );
};

export default Inbox;
