import React, {Fragment, useState} from "react";
import Header from "./Header";
import { Button } from "react-bootstrap";
import Mailer from "./Mailer";

const HomeComponent = () => {
    const [showMailLayout, setShowMailLayout] = useState(false);

    const mailLayoutHandler = (e) => {
        e.preventDefault();

        setShowMailLayout(!showMailLayout);
    }

    return(
        <Fragment>
            <Header />
            <Button variant="outline-warning" size="lg" onClick={mailLayoutHandler}>Compose Email!</Button>
            {showMailLayout && <Mailer />}
        </Fragment>
    );

};

export default HomeComponent;
